import React, {FunctionComponent} from "react";
import Layout from "../components/layout";
import {Container, Grid} from "../components/common";
import {Post} from "../utils/models";
import {Card} from "../components/card";
import styled from "styled-components";
import TagList from "../components/tag-list";
import {Link} from "gatsby";
import SidebarContent from "../components/sidebar-content";
import SEO from "../components/seo";
import Theme from "../styles/theme";
import SocialChannelList from "../components/social-channel-list";
import {graphql, useStaticQuery} from "gatsby";
import {SiteMetadata} from "../utils/models";
import {HomePanel} from '../components/home-panel';
import IntellRobotsLabTeam from "../../../../../content/assets/images/intellrobotslab_team.jpg"

interface PostsPageProps {
  pathContext: {
    posts: Post[];
    postsPerPage: number;
  };
  location: Location;
}

const HomeContainer = styled(Container)`
  grid-template-columns: minmax(0, 1fr) .25fr;
  grid-column-gap: 30px;
  text-align: center;
  max-width: 100%;
  font-weight: 400;
  font-size: 1.3rem;
  margin-top: 5%;
  margin-bottom: 5%;

  @media (max-width: ${Theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
  }
`;
const HomeText = styled(Container)`
  text-align: left;
  max-width: 50%;
  font-weight: 400;
  font-size: 1.3rem;
  position: relative;
  display: inline-block;

  @media (max-width: ${Theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
  }
`;
const HomeImage = styled(Container)`
  text-align: center;
  max-width: 50%;
  font-weight: 400;
  position: relative;
  display: inline-block;

  @media (max-width: ${Theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
  }
`;

const PostsContainer = styled(Grid)`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-areas: "latest latest" ". .";
  width: 100%;
  margin-left: 0;
  margin-right: 0;
  margin-top: -30px;

  @media (max-width: ${Theme.breakpoints.sm}) {
    display: block;
    padding: 0;

    article {
      margin-bottom: 30px;
    }
  }
`;

const Sidebar = styled.aside`
  width: 315px;
  padding-top: 30px;

  @media (max-width: ${Theme.breakpoints.xl}) {
    margin: 30px auto;
    border-top: 2px #e5eff5 solid;
    padding: 20px;
    width: 100%;
  }
`;

const ArchiveLinkWrapper = styled.div`
  grid-column: 1 / -1;
  text-align: center;
`;

const ArchiveLink = styled(Link)`
  font-size: .8em;
  padding: 10px;
  border-radius: .3em;
  transition: background-color .5s;
  background-color: #f2f2f2;

  &:hover {
    background-color: #e6e6e6;
  }
`;

const PostsPage: FunctionComponent<PostsPageProps> = ({ pathContext, location }) => {
  const posts = pathContext.posts.slice(0, pathContext.postsPerPage);
  const metadata = useStaticQuery<SiteMetadata>(graphql`
    query metadataQueryAndMetadataQuery {
      site {
        siteMetadata {
          author {
            name
            description
            social {
              facebook
              twitter
              linkedin
              instagram
              youtube
              github
              twitch
            }
          }
        }
      }
    }
  `);

  const author = metadata.site.siteMetadata.author;

  return (
    <Layout>
      <SEO location={location} type={`WebSite`} />
      <HomeContainer>
        <HomeText>
          We are <strong>Intelligent Robotics Lab</strong>, a team of robotics researchers
          from <strong>Rey Juan Carlos University</strong> experienced in participating in 
          several Robotics Competitions.<br /><br />

          We have more than 10 years of experience in development of robotic software, 
          development and ROS trainning; and more that 5 years of experience in ROS2.<br /><br />
        </HomeText>
        <HomeImage>
          <img src={IntellRobotsLabTeam} width="100%" alt=""/>
        </HomeImage>
      </HomeContainer>
      <HomePanel></HomePanel>
      <SocialChannelList channels={author.social}/>
    </Layout>
  );
};

export default PostsPage;
